class AddNew extends Component {
    constructor(props) {
        super(props);
        this.addTodo = this.addTodo.bind(this);
    };

    addTodo(event) {
        event.preventDefault();
        const thisObject = this;
        const eventText = document.getElementById("newToDo").value;

        thisObject.props.makeTodo(eventText);

        document.getElementById("newToDo").value = "";
    }

    render(){
        return (
            <div>
            <form id="addNew" onSubmit={this.addTodo}>
                <input  
                    type="text" id="newToDo" 
                    placeholder="add a new item">
                </input>
                <button id="button"> + </button>
            </form>
            <button onClick="sortTodos()" onKeyDown={this.handleKeyDown} id="sort"> sort </button>
            </div>
        );
    }
    }

export default AddNew;
